glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in mediump vec2 a_uv0;
out mediump vec2 v_uv0;
in vec4 a_color;
out vec4 v_color;
void main () {
  gl_Position = cc_matViewProj * vec4(a_position, 1);
  v_uv0 = a_uv0;
  v_color = a_color;
}

glsl3.frag:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
in mediump vec2 v_uv0;
uniform sampler2D texture;
uniform sampler2D u_fluxayTexture;
uniform ARGS {
  float u_time;
};
uniform User {
  vec4 u_lrbt;
  float rotated;
};
float linear(float x0, float x1, float y0, float y1, float inputX) {
    return (y1 - y0) * (inputX - x0) / (x1 - x0) + y0;
}
void main () {
  vec4 o = vec4(1, 1, 1, 1);
  vec2 uv = vec2(
    linear(u_lrbt[0], u_lrbt[1], 0.0, 1.0, v_uv0.x),
    linear(u_lrbt[2], u_lrbt[3], 0.0, 1.0, v_uv0.y)
  );
  if(rotated > 0.5)
  {
    float temp = uv.x;
    uv.x = uv.y;
    uv.y = 1.0 - temp;
  }
  #if USE_TEXTURE
  vec4 texture_tmp = texture(texture, v_uv0);
  #if CC_USE_ALPHA_ATLAS_texture
      texture_tmp.a *= texture(texture, v_uv0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    o.rgb *= (texture_tmp.rgb * texture_tmp.rgb);
    o.a *= texture_tmp.a;
  #else
    o *= texture_tmp;
  #endif
  #endif
  o *= v_color;
  ALPHA_TEST(o);
  if(o.a >= 1.0 && o.r>0.8 && u_time <= 2.0) {
    vec2 fluxayUV = vec2(uv.x, uv.y);
    fluxayUV.x -= u_time - 1.0;
    vec4 fluxay = texture2D(u_fluxayTexture, fluxayUV);
    gl_FragColor = o + fluxay;
  } else {
    gl_FragColor = o;
  }
}

